mapper.insert:

    select ifnull(max(reply_no), 0) + 1 as reply_no from reply where doc_no = #{docNo}

insert into reply (reply_no, doc_no, writer, content)
values (#{replyNo}, #{docNo}, #{writer}, #{content})

mapper.update:
update reply
set content = #{content}
where reply_no = #{replyNo}
  and doc_no = #{docNo}

mapper.delete:
delete
from reply
where doc_no = #{docNo}

    and reply_no = #{replyNo}
